Entry.Value:
// Mandelbox Formula by Tom Lowe (https://en.wikipedia.org/wiki/Mandelbox) with some 	simplification and with fixed scale=1.05.

public override long InSet(double x, double y, double z, 
                           double br, double bi, double bj, double bk, 
                           long zkl, bool invers) 
{

 double gr=50000;
 double xx, yy, zz;
 long tw=0;
 double r_n = 0;

 xx = x * x; yy = y * y; zz = z * z; 
 double r = Math.Sqrt(xx + yy + zz);
 double scale = 1.05;
 double fixedRadius = 1;
 double mult=1;
 double red=0;
 double green=0;
 double blue=0;

 for (int n = 1; n < zkl; n++) 
 {

//  fold box onto itself  
  if (x > 1)
    x = 2.0 - x;
  else if (x < -1)
    x = -2.0 - x;

  if (y > 1)
    y = 2.0 - y;
  else if (y < -1)
    y = -2.0 - y;

  if (z > 1)
    z = 2.0 - z;
  else if (z < -1)
    z = -2.0 - z;

// fold sphere onto itself

  xx=x*x;
  yy=y*y;
  zz=z*z;
  r = xx + yy + zz;

  if(r!=0) {
    red+=xx/r;
    green+=yy/r;
    blue+=zz/r;
  }

  if (r > gr) { 
    tw = n; break; 
  }

  mult=1;
  if (r < fixedRadius && r>0)
        mult=fixedRadius/r;

   mult*=scale;
   x*=mult;
   y*=mult;
   z*=mult;
   x+=br;
   y+=bi;
   z+=bj;
 }

if (invers) {
 if (tw == 0)
   tw = 1;
 else
   tw = 0;
}

if(tw>0) {
  additionalPointInfo.red=red;
  additionalPointInfo.green=green;
  additionalPointInfo.blue=blue;
}

return (tw);

}
