Entry.Value:
public override void Init() {
  base.Init();
  _scale=GetOrSetDouble("scale",1.1,"Mandelbox scale factor.");
}

double _scale=1.1;

// Mandelbox
public override long InSet( double x, double y, double z,  
                            double br, double bi, double bj, double bk, 
                            long zkl, bool inverse) 
{

 double bailout=50000;
 double xx, yy, zz;
 long returnValue=0;
 double r = 0;

 additionalPointInfo.red=0;
 additionalPointInfo.green=0;
 additionalPointInfo.blue=0;

 for (int n = 1; n < zkl; n++) 
 {

   bool xChanged=false;
   for (int i=0;i<5;i++) 
   {
     xChanged=false;
     if (x > 1)
     {
       x = 2.0 - x;
       xChanged=true;
     }
     else if (x < -1){
       x = -2.0 - x;
       xChanged=true;
   }
   if(!xChanged)
     break;
   }

   if (y > 1)            
     y = 2.0 - y;        
   else if (y < -1)             
     y = -2.0 - y;

   if (z > 1)              
     z = 2.0 - z;            
   else if (z < -1)             
     z = -2.0 - z;

   xx = x * x; yy = y * y; zz = z * z;
   r = xx + yy + zz;

   if(r!=0) 
   {
     additionalPointInfo.red+=xx/r;
     additionalPointInfo.green+=yy/r;
     additionalPointInfo.blue+=zz/r;
   }

   if (r > bailout) 
   {            
     returnValue = n; 
     break;          
   }

   double mult = _scale;
   if (r < 1 && r!=0)
     mult = _scale/r;

   x*=mult; y*=mult; z*=mult;
   if(x<0) 
     x=-x;
   x+=br; y+=bi; z+=bj;
 }

 if (inverse) 
 {         
  if (returnValue == 0)          
    returnValue = 1;          
  else            
    returnValue = 0;        
 }        
 return returnValue;    
}
