Entry.Value:
/// <summary>
        /// Initialisierung
        /// </summary>
        public override void Init() {
            base.Init();
            // Hier kann z.B. pow oder gr aus den Einstellungen gelesen werden.
additionalPointInfo=new AdditionalPointInfo();
        }


// Mandelbox
        public override long InSet(double ar, double ai, double aj,  double br, double bi, double bj, double bk, long zkl, bool invers) {

double gr=1112.5;
double xx, yy, zz;
          long tw;
          int n;
          double x=ar,y=ai,z=aj;
          double r_n = 0;

          xx = x * x; yy = y * y; zz = z * z; 
          tw = 0L;
          double r = Math.Sqrt(xx + yy + zz);
  double scale = -1.5;
//  double scale = 3.1415;
// double scale = 2.05;

//int xcount=0;
//int ycount=0;
//int zcount=0;

bool useRed=false;
bool useGreen=false;
bool useBlue=false;

additionalPointInfo.red=0;
additionalPointInfo.green=0;
additionalPointInfo.blue=0;

          for (n = 1; n < zkl; n++) {

useRed=false;
useGreen=false;
useBlue=false;

//              ; fold box onto itself  
              if (x > 1) {
                x = 2.0 - x;
useRed=true;
additionalPointInfo.red++;
}
              else if (x < -1) {
                x = -2.0 - x;
useRed=true;
additionalPointInfo.red++;
}
             
              if (y > 1) {
                y = 2.0 - y;
useRed=true;
additionalPointInfo.green++;

}
              else if (y < -1) {
                y = -2.0 - y;
useRed=true;
additionalPointInfo.green++;
}
             
              if (z > 1) {
                z = 2.0 - z;
useRed=true;
additionalPointInfo.blue++;
}
              else if (z < -1) {
                z = -2.0 - z;
useRed=true;
additionalPointInfo.blue++;
}

              
            //  ; fold sphere onto itself
              double fixedRadius = 1;
              double minRadius = 0.5;
      double length=   r = x*x + y*y + z*z;

    if (length > gr) { 
              tw = n; break; 
            }

   double mult=1;

    if (length < minRadius) {
        mult=fixedRadius/(minRadius*minRadius);
useGreen=true;
    }   else if (length < fixedRadius) {
        mult=fixedRadius/length;
useGreen=true;
  }
   mult*=scale;
   x*=mult;
   y*=mult;
   z*=mult;
 
if(!useRed && !useGreen)
useBlue=true;
//      length=   r = Math.Sqrt(x*x + y*y + z*z);
 x+=br;
   y+=bi;
   z+=bj;

/*   
if(useRed)
additionalPointInfo.red++;
if(useGreen)
additionalPointInfo.green+=4;
additionalPointInfo.blue=(int)(0.5*(additionalPointInfo.green+additionalPointInfo.red));
 */


          }


          if (invers) {
            if (tw == 0)
              tw = 1;
            else
              tw = 0;
          }
          return (tw);

        }
