Entry.Value:
/// <summary>
        /// Initialisierung
        /// </summary>
        public override void Init() {
            base.Init();
            // Hier kann z.B. pow oder gr aus den Einstellungen gelesen werden.
additionalPointInfo=new AdditionalPointInfo();
        }


// Mandelbox
        public override long InSet(double ar, double ai, double aj,  double br, double bi, double bj, double bk, long zkl, bool invers) {

double gr=1112.5;
double xx, yy, zz;
          long tw;
          int n;
          double x=ar,y=ai,z=aj;
          double r_n = 0;

          xx = x * x; yy = y * y; zz = z * z; 
          tw = 0L;
          double r = Math.Sqrt(xx + yy + zz);
  double scale = -2;

int xcount=0;
int ycount=0;
int zcount=0;

          for (n = 1; n < zkl; n++) {

//              ; fold box onto itself  
              if (x > 1) {
                x = 2.0 - x;
xcount++;
}
              else if (x < -1) {
                x = -2.0 - x;
xcount++;
}
             
              if (y > 1) {
                y = 2.0 - y;
ycount++;
}
              else if (y < -1) {
                y = -2.0 - y;
ycount++;
}
             
              if (z > 1) {
                z =  z-2.0;
x=-x;
zcount++;
}
              else if (z < -1) {
                z = 2.0 + z;
x=-x;
zcount++;
}

              
            //  ; fold sphere onto itself
              double fixedRadius = 1;
              double minRadius = 0.5;
      double length=   r = x*x + y*y + z*z;

   double mult=1;

    if (length < minRadius) {
        mult=fixedRadius/minRadius;
    }   else if (length <( fixedRadius*fixedRadius)) {
        mult=fixedRadius/length;
  }
   mult*=scale;
   x*=mult;
   y*=mult;
   z*=mult;
 

      length=   r = Math.Sqrt(x*x + y*y + z*z);
 x+=br;
   y+=bi;
   z+=bj;
       if (length > gr) { 
              tw = n; break; 
            }

 


          }


additionalPointInfo.red=xcount;
additionalPointInfo.green=ycount;
additionalPointInfo.blue=zcount;



          if (invers) {
            if (tw == 0)
              tw = 1;
            else
              tw = 0;
          }
          return (tw);

        }
