Entry.Value:
/// <summary>
        /// Initialisierung
        /// </summary>
        public override void Init() {
            base.Init();
            // Hier kann z.B. pow oder gr aus den Einstellungen gelesen werden.
additionalPointInfo=new AdditionalPointInfo();
        }


// Mandelbox
        public override long InSet(double ar, double ai, double aj,  double br, double bi, double bj, double bk, long zkl, bool invers) {

double gr=1112.5;
double xx, yy, zz;
double aar=0,aai=0,aaj=0;
          long tw;
          int n;
          double x=0,y=0,z=aj;
          double r_n = 0;

          xx = x * x; yy = y * y; zz = z * z; 
          tw = 0L;
          double r = Math.Sqrt(xx + yy + zz);
  double scale = ar;
double lpow=ai; // die Potenz der Inversion am Kreis
int xcount=0;
int ycount=0;
int zcount=0;

          for (n = 1; n < zkl; n++) {

 

//              ; fold box onto itself  
              if (x > 1) {
                x = 2.0 - x;
//y=-z;
//z=-y;
xcount++;
}
              else if (x < -1) {
                x = -2.0 - x;
//y=-z;
//z=-y;
xcount++;
}
             
              if (y > 1) {
                y = 2.0 - y;
//x=-z;
//z=-x;
ycount++;
}
              else if (y < -1) {
                y = -2.0 - y;
//x=-z;
//z=-x;
ycount++;
}
             
              if (z > 1) {
                z = 2.0 - z;
//x=-y;
//y=-x;
zcount++;
}
              else if (z < -1) {
                z = -2.0 - z;
//x=-y;
//y=-x;
zcount++;
}


/*
   xx = x * x; yy = y * y; zz = z * z; 

  x = 2 *x * y * z ;
               y = zz - (yy + xx) / 2.0 ;
                z = xx - (yy + zz) / 2.0 ;
*/


              
            //  ; fold sphere onto itself
              double fixedRadius = 1;
              double minRadius = 0.5;
      double length=   r = x*x + y*y + z*z;

   double mult=1;

double lpow_lenght=Math.Pow(r,lpow);
    if (length < minRadius) {
        mult=fixedRadius/(minRadius*minRadius);
    }   else if (length <( fixedRadius*fixedRadius)) {
        mult=fixedRadius/( lpow_lenght);
  //     mult=fixedRadius/(length*length);
  }
   mult*=scale;
   x*=mult;
   y*=mult;
   z*=mult;


// aar = x * x; aai = y * y;aaj= z * z;

//y=y*x;
 //               y += y;
//                x= aar - aai;

//                y = 2.0 * x * y ;
 //               z = 2.0 * x* z;
  //              x = aar - aai - aaj;
 

 x+=br;
   y+=bi;
   z+=bj;

   xx = x * x; yy = y * y; zz = z * z; 

      length=   r = Math.Sqrt(xx + yy + zz);

       if (length > gr) { 
              tw = n; break; 
            }
          }

additionalPointInfo.red=xcount;
additionalPointInfo.green=ycount;
additionalPointInfo.blue=zcount;

          if (invers) {
            if (tw == 0)
              tw = 1;
            else
              tw = 0;
          }
          return (tw);

        }
