Entry.Value:
public override void Init() {
  base.Init();
  additionalPointInfo=new AdditionalPointInfo();
}


// Mandelbox Formula
public override long InSet(double ar, double ai, double aj,  double br, double bi, double bj, double bk, long zkl, bool invers) {

  double gr=50000;
  double xx, yy, zz;
  long tw=0;
  int n;
  double x=0,y=0,z=0;
   xx = x * x; yy = y * y; zz = z * z; 
   double r =xx + yy + zz;
  double scale = GetDouble("Formula.Mandelbox.Scale");
  additionalPointInfo.red=0; 
  additionalPointInfo.green=0;
  additionalPointInfo.blue=0;

  for (n = 1; n < zkl; n++) {
     
     //  fold box onto itself  
              
  if (x > 1)
    x = 2.0 - x;
  else if (x < -1)
    x = -2.0 - x;
             
  if (y > 1)
    y = 2.0 - y;
  else if (y < -1)
    y = -2.0 - y;
             
  if (z > 1)
   z = 2.0 - z;
  else if (z < -1)
    z = -2.0 - z;
              
    //  fold sphere onto itself
      double minRadius = 0.00001;
      r = Math.Pow(x*x + y*y + z*z,aj);

  if(r!=0) {
    additionalPointInfo.red+=x*x/r;
    additionalPointInfo.green+=y*y/r;
    additionalPointInfo.blue+=z*z/r;
  }
  if (r > gr) { 
    tw = n; break; 
  }

  double mult=1;

 if (r < 1 && r>minRadius) {
        mult=1/r;
  }
   mult*=scale;
   x*=mult;
   y*=mult;
   z*=mult;
   x+=br;
   y+=bi;
   z+=bj;
   }

   if (invers) {
      if (tw == 0)
        tw = 1;
      else
         tw = 0;
       }
      return (tw);
   }
