Entry.Value:
int gr=20;
  public override void Init() {
            base.Init();
           additionalPointInfo=new AdditionalPointInfo();
  }


     public override long InSet(double ar, double ai, double aj, double br, double bi, double bj, double bk, long zkl, bool invers) {

          double xx, yy, zz;
          long tw;
          int n;
          double x = ar, y = ai, z = aj;

          xx = x * x; yy = y * y; zz = z * z;
          tw = 0;
          double r = Math.Sqrt(xx + yy + zz);
          Fractrace.Geometry.VecRotation vecRot = new Fractrace.Geometry.VecRotation();

additionalPointInfo.red=0;
additionalPointInfo.green=0;
additionalPointInfo.blue=0;
          for (n = 1; n < zkl; n++) {

// 1. Rotation

            double phi = Math.Atan2(y, x);
       double phi2 = Math.Atan2(y, z);

double mult=1;
            vecRot.y = z;
            vecRot.x =0;
            vecRot.z =-y;

double zMult=-1;
if(y<0)
zMult=1;
//if(z!=0)
//zMult=Math.Sqrt(Math.Abs(z));
            vecRot.angle =zMult*phi;
//if(z<0)
   //         vecRot.angle=  -vecRot.angle;
            vecRot.Normalize();
            Fractrace.Geometry.Vec3 pos=new Fractrace.Geometry.Vec3(x,y,z);
            Fractrace.Geometry.Vec3 newPos= vecRot.getTransform(pos);
            x = newPos.X;
            y = newPos.Y;
            z = newPos.Z;
   //         xx = x * x; yy = y * y; zz = z * z;// aak = ak * ak;
           r = Math.Sqrt(xx + yy + zz);
//r=xx+yy+zz;
/*
     x *= r;
            y *= r;
            z *= r;*/
// 2. Rotation

            phi = Math.Atan2(y, x);
       phi2 = Math.Atan2(y, z);

mult=1;
            vecRot.y = 0;
            vecRot.x =0;
            vecRot.z =-1;

            vecRot.angle =-phi;
            vecRot.Normalize();
            pos=new Fractrace.Geometry.Vec3(x,y,z);
             newPos= vecRot.getTransform(pos);
            x = newPos.X;
            y = newPos.Y;
            z = newPos.Z;


            xx = x * x; yy = y * y; zz = z * z;// aak = ak * ak;
//            r = Math.Sqrt(xx + yy + zz);
r=xx+yy+zz;



r*=r;
if(r!=0) {
additionalPointInfo.red+=x*x/r;
additionalPointInfo.green+=y*y/r;
additionalPointInfo.blue+=z*z/r;
}
            x *= r;
            y *= r;
            z *= r;
            y += bj;
            x += br;
            z += bi;

            if (r > gr) {
              tw = n; break;
            }
          }


// Switch inner and outer view
          if (invers) {
            if (tw == 0)
              tw = 1;
            else
              tw = 0;
          }
          return (tw);
        }
