Entry.Value:
int gr=20;

/// <summary>
        /// Initialisierung
        /// </summary>
        public override void Init() {
            base.Init();
            // Hier kann z.B. pow oder gr aus den Einstellungen gelesen werden.

additionalPointInfo=new AdditionalPointInfo();
para1=GetDouble("Formula.Universal.Para1");
para2=GetDouble("Formula.Universal.Para2");
para3=GetDouble("Formula.Universal.Para3");
para4=GetDouble("Formula.Universal.Para4");
para5=GetDouble("Formula.Universal.Para5");

        }

double para1=0.5;
double para2=1;
double para3=2;
double para4=0.5;
double para5=0.5;


        public override long InSet(double ar, double ai, double aj, double br, double bi, double bj, double bk, long zkl, bool invers) {


            double aar, aai, ai_alt, aj_alt, aaj, cj, ck, ari, arj, ark, aik, aij, ajk, ak_alt;
            double c, d;
            long tw;
            int n;

additionalPointInfo.red=0;
additionalPointInfo.green=0;
additionalPointInfo.blue=0;

double ak=para4,aak=0,al=0,aal=0;
//ai=0.1;
            aar = ar * ar; aai = ai * ai; aaj = aj * aj; tw = 0L;
            d = aar + aai + aaj;
            for (n = 1; n < zkl; n++) {
                ai = para3 * ai * ar * aj;
                aj =para2* aaj - (aai + aar) *para1;
                ar =para5* aar - (aai + aaj) *para4;

double aiTemp=ai;
ai=aj;
//aj=ar;
//ar=aiTemp;
aj=aiTemp;

if(n%3==0) {
ar +=br;
    ai +=bi;
    aj +=bj;

}


if(n%3==1) {
ar -=br;
    ai +=bj;
    aj +=bi;

}


                aar = ar * ar; aai = ai * ai; aaj = aj * aj;
                d = aar + aai + aaj;

// Surface Color
double ar2=ar; if(ar<0) ar2=-ar;
double ai2=ai; if(ai<0) ai2=-ai;
double aj2=aj; if(aj<0) aj2=-aj;

if(d>0) {
additionalPointInfo.red+=ar2/d;
additionalPointInfo.green+=ai2/d;
additionalPointInfo.blue+=aj2/d;
}

                if ((d) > gr) { tw = n; break; }
            }
            return (tw);

        }
