Entry.Value:
/// <summary>
        /// Initialisierung
        /// </summary>
        public override void Init() {
            base.Init();

additionalPointInfo=new AdditionalPointInfo();
            // Hier kann z.B. pow oder gr aus den Einstellungen gelesen werden.
        }


// Mandelbox
        public override long InSet(double ar, double ai, double aj,  double br, double bi, double bj, double bk, long zkl, bool invers) {

double gr=110;
double xx, yy, zz;
          long tw;
          int n;
          double x=ar,y=ai,z=aj;
     //        double x=br,y=bj,z=bi;
          xx = x * x; yy = y * y; zz = z * z;
          tw = 0L;
          double m= xx + yy + zz;
additionalPointInfo.red=0;
additionalPointInfo.green=0;
additionalPointInfo.blue=0;
          for (n = 1; n < zkl; n++) {
if(m!=0) {
x=x/m+br;
y=Math.Abs(y)/m+bi;
z=Math.Abs(z)/m+bj;
} else {
 x+=br;
 y+=bi;
 z+=bj;
}

          xx = x * x; yy = y * y; zz = z * z;
m= xx + yy + zz;
if(m!=0) {
additionalPointInfo.red+=xx/m;
additionalPointInfo.green+=yy/m;
additionalPointInfo.blue+=zz/m;
}
            if (m > gr) { 
              tw = n; break; 
            }

          }


          if (invers) {
            if (tw == 0)
              tw = 1;
            else
              tw = 0;
          }
          return (tw);

        }
