Entry.Value:
public override void Init() {
            base.Init();
             additionalPointInfo=new AdditionalPointInfo();
}


// Mandelbox
        public override long InSet(double ar, double ai, double aj,  double br, double bi, double bj, double bk,                        long zkl, bool invers) {

  double gr=50000;
  long tw=0;
  int n;
  double x=ar,y=ai,z=aj;
  double xx=0, yy=0, zz=0;
//  double r_n = 0;
   double r = Math.Sqrt(xx + yy + zz);
   double scale = 1.04;
   double mult=1;

  additionalPointInfo.red=0;
  additionalPointInfo.green=0;
  additionalPointInfo.blue=0;


   for (n = 1; n < zkl; n++) {

//  fold box onto itself  
   if (x > 1)
      x = 2.0 - x;
   else if (x < -1)
       x = -2.0 - x;
             
    if (y > 1)
       y = 2.0 - y;
    else if (y < -1)
        y = -2.0 - y;
         
  if (z > 1)
     z = 2.0 - z;
  else if (z < -1)
      z = -2.0 - z;

              
// Bailout and colors:
 
  xx=x*x;
  yy=y*y;
  zz=z*z;
  r = xx + yy + zz;

  if(r!=0) {
    additionalPointInfo.red+=xx/r;
    additionalPointInfo.green+=yy/r;
    additionalPointInfo.blue+=zz/r;
  }

     if (r > gr) { 
         tw = n; break; 
      }

     //  fold sphere onto itself

    mult=1;
    if (r < 1.0 && r>0) {
        mult=Math.Sqrt(1.0)/r;
    }
   mult*=scale;
   x*=mult;
   y*=mult;
   z*=mult;
   x+=br;
   y+=bi;
   z+=bj;
 }

   if (invers) {
     if (tw == 0)
      tw = 1;
     else
       tw = 0;
    }
    
   return (tw);

        }
