Entry.Value:
/// <summary>
        /// Initialisierung
        /// </summary>
        public override void Init() {
            base.Init();
            // Hier kann z.B. pow oder gr aus den Einstellungen gelesen werden.
additionalPointInfo=new AdditionalPointInfo();
          gr1=GetDouble("Formula.Static.Cycles");
int tempGr=(int)gr1;
gr1=gr1- tempGr;
gr1=1-gr1;
gr1*=2.4;

        }

 double gr1=0;

        public override long InSet(double ar, double ai, double aj,  double br, double bi, double bj, double bk, long zkl, bool invers) {
            double aar, aai, aaj;
            long tw;
            int n;
//            int pow = 8; // n=8 entspricht dem Mandelbulb
              int pow = 2; // n=8 entspricht dem Mandelbulb
                double gr =Math.Pow(10,gr1)+1.0;  //   10; // Ab diesem Wert liegt mit Sicherheit Nichtzugehörigkeit zur Menge vor.
            double theta, phi;

            double r_n = 0;
            aar = ar * ar; aai = ai * ai; aaj = aj * aj;
            tw = 0L;
            double r = Math.Sqrt(aar + aai + aaj);

             double      phi_pow;
             double       theta_pow;
             double    sin_theta_pow;
              double  rn_sin_theta_pow;
double minR=1000;

if(bj>0.1) return 1;
//if(bj<0) return 0;
//if(bj<0.5)
bj=bj*55.0;


additionalPointInfo.red=0;
additionalPointInfo.green=0;
additionalPointInfo.blue=0;

double red=0; double green=0; double blue=0;

double piAdd=Math.PI/2.0;
            for (n = 1; n < zkl; n++) {

                theta = Math.Atan2(Math.Sqrt(aar + aai), aj);
                phi = Math.Atan2(ai, ar);
                //r_n = Math.Pow(r, pow);
                r_n=r;


                phi_pow=phi*pow;
                theta_pow=theta*pow;
                sin_theta_pow=Math.Sin(theta_pow+piAdd);
                rn_sin_theta_pow=r_n* sin_theta_pow;


                ar =  rn_sin_theta_pow * Math.Cos(phi_pow+Math.PI)+br;
              //  ai = rn_sin_theta_pow * Math.Sin(phi_pow+Math.PI)+bi;
                 ai = rn_sin_theta_pow * Math.Sin(phi_pow+Math.PI)+bi;
                aj = r_n * Math.Cos(theta_pow+piAdd)+bj;

                aar = ar * ar; aai = ai * ai; aaj = aj * aj;
                //r = Math.Sqrt(aar + aai + aaj);
r = aar + aai + aaj;


//red=aar;
//green=aai;
//blue=aaj;
red=0.01;
green=0.01;
if(minR>r)
minR=r;
blue=minR;


                if (r > gr) { tw = n; break; }

            }

additionalPointInfo.red=red;
additionalPointInfo.green=green;
additionalPointInfo.blue=blue;


            if (invers) {
                if (tw == 0)
                    tw = 1;
                else
                    tw = 0;
            }

//if(red<=blue || red<=green) {tw=1;}
            return (tw);

        }
