Entry.Value:
/// <summary>
        /// Initialisierung
        /// </summary>
        public override void Init() {
            base.Init();
            // Hier kann z.B. pow oder gr aus den Einstellungen gelesen werden.
additionalPointInfo=new AdditionalPointInfo();
        }


        public override long InSet(double ar, double ai, double aj,  double br, double bi, double bj, double bk, long zkl, bool invers) {
            double aar, aai, aaj;
            long tw;
            int n;
            int pow = 8; // n=8 entspricht dem Mandelbulb
            double gr = 10; // Ab diesem Wert liegt mit Sicherheit Nichtzugehörigkeit zur Menge vor.
            double theta, phi;

            double r_n = 0;
            aar = ar * ar; aai = ai * ai; aaj = aj * aj;
            tw = 0L;
            double r = Math.Sqrt(aar + aai + aaj);

             double      phi_pow;
             double       theta_pow;
             double    sin_theta_pow;
              double  rn_sin_theta_pow;

double oldx=ar;
double oldy=ai;
double oldz=aj;
double dx=0;
double dy=0;
double dz=0;


            for (n = 1; n < zkl; n++) {

                theta = Math.Atan2(Math.Sqrt(aar + aai), aj);
                phi = Math.Atan2(ai, ar);
                r_n = Math.Pow(r, pow);

                phi_pow=phi*pow;
                theta_pow=theta*pow;
                sin_theta_pow=Math.Sin(theta_pow);
               rn_sin_theta_pow=r_n* sin_theta_pow;


                ar =  rn_sin_theta_pow * Math.Cos(phi_pow)+br;
                ai = rn_sin_theta_pow * Math.Sin(phi_pow)+bi;
                aj = r_n * Math.Cos(theta_pow)+bj;

                aar = ar * ar; aai = ai * ai; aaj = aj * aj;
                r = Math.Sqrt(aar + aai + aaj);

                if (r > gr) { tw = n; break; }

dx=ar-oldx;
dy=ai-oldy;
dz=aj-oldz;

double dist=dx*dx+dy*dy+dz*dz;
double dzykl=3.0*(double)n/((double)zkl);
if(dzykl<1)
additionalPointInfo.red+=dist;
else if(dzykl<2)
additionalPointInfo.green+=dist;
else
additionalPointInfo.blue+=dist;



oldx=ar;
oldy=ai;
oldz=aj;

            }

            if (invers) {
                if (tw == 0)
                    tw = 1;
                else
                    tw = 0;
            }
            return (tw);

        }
