Entry.Value:
/// <summary>
        /// Initialisierung
        /// </summary>
        public override void Init() {
            base.Init();
            // Hier kann z.B. pow oder gr aus den Einstellungen gelesen werden.
additionalPointInfo=new AdditionalPointInfo();
          gr1=GetDouble("Formula.Static.Cycles");
int tempGr=(int)gr1;
gr1=gr1- tempGr;
gr1=1-gr1;
gr1*=2.4;

    gr2=GetDouble( "Formula.Static.MinCycle");
int tempGr2=(int)gr2;
gr2=gr2- tempGr2;
gr2=1-gr2;
gr2*=2.4;

//Set("Formula.Mandelbulb.Pow1",8);
//Set("Formula.Mandelbulb.Pow2",8);
//Set("Formula.Mandelbulb.Pow3",8);
 pow1=GetDouble("Formula.Mandelbulb.Pow1");
 pow2=GetDouble("Formula.Mandelbulb.Pow2");
 pow3=GetDouble("Formula.Mandelbulb.Pow3");




        }

 double gr1=0;
double gr2=0;

double pow1=8;
double pow2=8;
double pow3=8;


        public override long InSet(double ar, double ai, double aj,  double br, double bi, double bj, double bk, long zkl, bool invers) {
            double aar, aai, aaj;
            long tw;
            int n;
//            int pow = 8; // n=8 entspricht dem Mandelbulb
                double gr =Math.Pow(10,gr1)+1.0;  //   10; // Ab diesem Wert liegt mit Sicherheit Nichtzugehörigkeit zur Menge vor.
if(invers) {
gr =Math.Pow(10,gr2)+1.0; 
}
            double theta, phi;

            double r_n = 0;
            aar = ar * ar; aai = ai * ai; aaj = aj * aj;
            tw = 0L;
            double r = Math.Sqrt(aar + aai + aaj);

             double      phi_pow;
             double       theta_pow;
             double    sin_theta_pow;
              double  rn_sin_theta_pow;

additionalPointInfo.red=0;
additionalPointInfo.green=0;
additionalPointInfo.blue=0;

double red=0; double green=0; double blue=0;

            for (n = 1; n < zkl; n++) {

                theta = Math.Atan2(Math.Sqrt(aar + aai), aj);
                phi = Math.Atan2(ai, ar);
                r_n = Math.Pow(r, pow1);

                phi_pow=phi*pow2;
                theta_pow=theta*pow3;
                sin_theta_pow=Math.Sin(theta_pow);
               rn_sin_theta_pow=r_n* sin_theta_pow;


                ar =  rn_sin_theta_pow * Math.Cos(phi_pow)+br;
                ai = rn_sin_theta_pow * Math.Sin(phi_pow)+bi;
                aj = r_n * Math.Cos(theta_pow)+bj;

                aar = ar * ar; aai = ai * ai; aaj = aj * aj;
                r = Math.Sqrt(aar + aai + aaj);


if(n>4) {
double ar2=ar; if(ar<0) ar2=-ar;
double ai2=ai; if(ai<0) ai2=-ai;
double aj2=aj; if(aj<0) aj2=-aj;

if(n>zkl-1) {
red+=ar2/r;
green+=ai2/r;
blue+=aj2/r;
} else {
red+=gr1/2.4*ar2/r;
green+=gr1/2.4*ai2/r;
blue+=gr1/2.4*aj2/r;
}
}

                if (r > gr) { tw = n; break; }

            }

additionalPointInfo.red=red;
additionalPointInfo.green=green;
additionalPointInfo.blue=blue;


            if (invers) {
                if (tw == 0)
                    tw = 1;
                else
                    tw = 0;
            }
            return (tw);

        }
