Entry.Value:
        public override void Init() {
            base.Init();
           additionalPointInfo=new AdditionalPointInfo();
           //Set("Formula.BenesiMandelbulb.TestPara",8);
        }


// Bailout
 double gr=20;


// No idea, what flip is :-(
public double flip(double r) {
//return r;
return abs(r);
}

double abs(double r) {
if(r<0)
return -r;
return r;
}

double sqrt(double r) {
return Math.Sqrt(r);
}

double sqr(double r) {
return Math.Sqrt(r);
}

double sin(double r) {
return Math.Sin(r);
}

double cos(double r) {
return Math.Cos(r);
}

double atan2(double x,double y) {
if(x==0 && y==0)
return 0;
return Math.Atan2(x,y);
}

        public override long InSet(double sx, double sy, double sz,  double cr, double ci, double cj, double additional, long zkl, bool invers) {

additionalPointInfo.red=0;
additionalPointInfo.green=0;
additionalPointInfo.blue=0;

double szz=sz*sz;
double syy=sy*sy;
double sxx=sx*sx;
double sy2=sy;
double sz2=sz;
double nx=0;
double ny=0;
double ny2=0;
double nz=0;
double nz2=0;

double theta=0;
double phi=0;
double bail=sqrt(sxx+syy+szz);
double r=bail;
double v=5;
int tw=0;  
double pow=5;

//if(ci>0) return 1;
            for (int n = 1; n < zkl; n++) {

double r2=Math.Sqrt(sx*sx+sy*sy+sz*sz);
r2=Math.Pow(r2,pow);

theta=atan2(sx,flip(sy));
double whiskey=atan2(sx,flip(sz));
double tango=atan2(sx,flip(sqrt(sy2*sy2+sz2*sz2)));
double foxtrot=atan2(sy2,flip(sz2));

nx=r2*cos(tango*v);
ny=r2*sin(theta*v);
nz=r2*sin(whiskey*v);
ny2=r2*cos(foxtrot*v)*sin(tango*v);
nz2=r2*sin(foxtrot*v)*sin(tango*v);

	sx=nx+cr;
	sy=ny+ci;
	sz=nz+cj;
	sy2=ny2+ci;
	sz2=nz2+cj;
	
r=bail=abs(sx)+abs(sy)+abs(sz);
//z=quaternion(sx,sy,sz,0);   // just for ChaosPro orbital coloring scheme

// Old Stuff:
/*
double r2=Math.Pow(r, pow);
theta=atan2(sx,flip(sqrt(sz*sz+sy*sy)));
phi=atan2(sy,flip(sz));
			
double nx=r2*Math.Cos(v*theta);
double ny=r2*sin(v*theta)*Math.Cos(v*phi);
double nz=r2*sin(v*theta)*Math.Sin(v*phi);
				
	sx=nx+cr;
	sy=ny+ci;
	sz=nz+cj;

szz=sz*sz;syy=sy*sy;sxx=sx*sx; 

//z=quaternion(sx,sy,sz,0);
r=bail=sqrt(sxx+syy+szz);
*/

if(n>2) {
additionalPointInfo.red+=abs(sx)/bail;
additionalPointInfo.green+=abs(sy)/bail;
additionalPointInfo.blue+=abs(sz)/bail;
}

                if (bail > gr) { tw = n; break; }

            }

            if (invers) {
                if (tw == 0)
                    tw = 1;
                else
                    tw = 0;
            }
            return (tw);

        }
