Entry.Value:
/// <summary>
        /// Initialisierung
        /// </summary>
        public override void Init() {
            base.Init();
            // Hier kann z.B. pow oder gr aus den Einstellungen gelesen werden.

  additionalPointInfo=new AdditionalPointInfo();
        }


        public override long InSet(double ar, double ai, double aj,  double br, double bi, double bj, double bk, long zkl, bool invers) {

double gr=2;
int pow = 2; // n=8 entspricht dem Mandelbulb
double xx, yy, zz;
          long tw;
          int n;
//          ai = 0; aj = 0; ak = 0;

          double x=ar,y=ai,z=aj;
          double r_n = 0;

          xx = x * x; yy = y * y; zz = z * z; 
          tw = 0L;
          double r = Math.Sqrt(xx + yy + zz);
/*
if(bi>0)
return 1;
bi=0.1*bi;
*/


additionalPointInfo.red=0;additionalPointInfo.green=0;additionalPointInfo.blue=0;
          for (n = 1; n < zkl; n++) {


            
          double r_xy=Math.Sqrt(xx+yy);
  //  double r_xy=xx+yy;
                     

//1. Runde
  double a = 1;
            
            if(r_xy!=0.0)
             a= 1 - zz *( r_xy);

            y=2 *x*y*a;
            x = (xx - yy) * a;
            z = 2 * z * r_xy;


double tempX=x;
x=y;
y=z;
z=tempX;

    xx = x * x; yy = y * y; zz = z * z;

// 2.Runde 
  a = 1;
            
            if(r_xy!=0.0)
             a= 1 - zz *( r_xy);

            y=2 *x*y*a;
            x = (xx - yy) * a;
            z = 2 * z * r_xy;


tempX=x;
x=y;
y=z;
z=tempX;

    xx = x * x; yy = y * y; zz = z * z;

// 3.Runde 
  a = 1;
            
            if(r_xy!=0.0)
             a= 1 - zz *( r_xy);

            y=2 *x*y*a;
            x = (xx - yy) * a;
            z = 2 * z * r_xy;


tempX=x;
x=y;
y=z;
z=tempX;

//    xx = x * x; yy = y * y; zz = z * z;

x+=br;
y+=bi;
z+=bj;

            xx = x * x; yy = y * y; zz = z * z;
            r = Math.Sqrt(xx + yy + zz);
            
if(n>3 && n<50) {

if(x>0)
additionalPointInfo.red+=x/r;
if(y>0)
additionalPointInfo.green+=y/r;
if(z>0)
additionalPointInfo.blue+=z/r;

if(x<0)
additionalPointInfo.red-=x/r;
if(y<0)
additionalPointInfo.green-=y/r;
if(z<0)
additionalPointInfo.blue-=z/r;
}


            if (r > gr) { 
              tw = n; break; 
            }

          }

          if (invers) {
            if (tw == 0)
              tw = 1;
            else
              tw = 0;
          }
          return (tw);

        }
