Entry.Value:
/// <summary>
        /// Initialisierung
        /// </summary>
        public override void Init() {
            base.Init();
            // Hier kann z.B. pow oder gr aus den Einstellungen gelesen werden.
        }


// Pow 2- Variante ohne Winkelfunktionen
        public override long InSet(double ar, double ai, double aj,  double br, double bi, double bj, double bk, long zkl, bool invers) {
//      public long InSet2(double ar, double ai, double aj,  double br, double bi, double bj, double bk, long zkl, bool invers) {


double gr=2;
int pow = 2; // n=8 entspricht dem Mandelbulb
double xx, yy, zz;
          long tw;
          int n;
//          ai = 0; aj = 0; ak = 0;

          double x=0,y=0,z=0;
          double r_n = 0;

          xx = x * x; yy = y * y; zz = z * z; 
          tw = 0L;
          double r = Math.Sqrt(xx + yy + zz);

          for (n = 1; n < zkl; n++) {


  double scale = 2.0;
//              ; fold box onto itself  
              if (x > 1)
                x = 2.0 - x;
              else if (x < -1)
                x = -2.0 - x;
             
              if (y > 1)
                y = 2.0 - y;
              else if (y < -1)
                y = -2.0 - y;
             
              if (z > 1)
                z = 2.0 - z;
              else if (z < -1)
                z = -2.0 - z;
              
            //  ; fold sphere onto itself
              double fixedRadius = 1;
              double minRadius = 0.5;


//              double length = point.Magnitude()

            xx = x * x; yy = y * y; zz = z * z;// aak = ak * ak;
       double length=   r = Math.Sqrt(xx + yy + zz);
double mult=1;
              if (length < minRadius) {
mult=Math.Sqrt(fixedRadius)/Math.Sqrt(minRadius);
//                point.MultiplyEquals(sqr(fixedRadius)/sqr(minRadius))
x*=mult;
y*=mult;
z*=mult;
}
              else if (length < fixedRadius) {
mult=Math.Sqrt(fixedRadius)/Math.Sqrt(length);
//                point.MultiplyEquals(sqr(fixedRadius)/sqr(length))
x*=mult;
y*=mult;
z*=mult;

}
x*=scale;
y*=scale;
z*=scale;

//              point.MultiplyEquals(scale)
x+=br;
y+=bi;
z+=bj;
//              point.AddEquals(C)
     xx = x * x; yy = y * y; zz = z * z;// aak = ak * ak;
       r = Math.Sqrt(xx + yy + zz);

            if (r > gr) { 
              tw = n; break; 
            }

          }

          if (invers) {
            if (tw == 0)
              tw = 1;
            else
              tw = 0;
          }
          return (tw);

        }
