Entry.Value:
/// <summary>
        /// Initialisierung
        /// </summary>
        public override void Init() {
            base.Init();
            // Hier kann z.B. pow oder gr aus den Einstellungen gelesen werden.
additionalPointInfo=new AdditionalPointInfo();

        }

        public override long InSet(double ar, double ai, double aj,  double br, double bi, double bj, double bk, long zkl, bool invers) {
            double aar, aai, aaj;
            long tw;
            int n;
            int pow = 7; // n=8 entspricht dem Mandelbulb
                double gr =100;  //   10; // Ab diesem Wert liegt mit Sicherheit Nichtzugehörigkeit zur Menge vor.

            aar = ar * ar; aai = ai * ai; aaj = aj * aj;
            tw = 0L;
            double r = Math.Sqrt(aar + aai + aaj);

additionalPointInfo.red=0;
additionalPointInfo.green=0;
additionalPointInfo.blue=0;

double red=0; double green=0; double blue=0;


double ar_=ar;
double aj_=aj;
double ai_=ai;

double divider=0;
            for (n = 1; n < zkl; n++) {
                
               divider=ar+ai+aj;
               if(divider!=0)
                ar_=(ar*ar+ai*ai+aj*aj)/divider;

               divider=ar*ar+ai*ai+aj*aj;
               if(divider!=0)
                ai_=(ar*ar*ar+ai*ai*ai+aj*aj*aj)/divider;

               divider=ar*ar*ar+ai*ai*ai+aj*aj*aj;
               if(divider!=0)
                aj_=(ar*ar*ar*ar+ai*ai*ai*ai+aj*aj*aj*aj)/divider;

                ar=ar_;ai=ai_;aj=aj_;
                aar = ar * ar; aai = ai * ai; aaj = aj * aj;
                r =aar + aai + aaj;

/*
if(n<=zkl-2 && n>=zkl-4) {
red+=aar/r;
green+=aai/r;
blue+=aaj/r;
}
//if(invers)


if(n==zkl-1) {
red+=grFrac*aar/r;
green+=grFrac*aai/r;
blue+=grFrac*aaj/r;
}
if(n==zkl-5) {
red+=(1-grFrac)*aar/r;
green+=(1-grFrac)*aai/r;
blue+=(1-grFrac)*aaj/r;
}
*/
r= Math.Sqrt(r);
                if (r > gr) { tw = n; break; }

            }

additionalPointInfo.red=red;
additionalPointInfo.green=green;
additionalPointInfo.blue=blue;


            if (invers) {
                if (tw == 0)
                    tw = 1;
                else
                    tw = 0;
            }

/*
if(red<=blue || red<=green) {
tw=1;
}*/
            return (tw);

        }
