Entry.Value:
        public override void Init() {
            base.Init();
            additionalPointInfo=new AdditionalPointInfo();
       
        }

        public override long InSet(double ar, double ai, double aj,  double br, double bi, double bj, double bk, long zkl, bool invers) {
            double aar, aai, aaj;
            long tw=0;
            int n;
           double gr = 10; 

            aar = ar * ar; aai = ai * ai; aaj = aj * aj;
            double r = Math.Sqrt(aar + aai + aaj);
additionalPointInfo.red=0;additionalPointInfo.green=0;additionalPointInfo.blue=0;
double red=0; double green=0; double blue=0;

// if(bi>0) return 1 ;

            for (n = 1; n < zkl; n++) {

// Mandelbrot of (x,y)-z Axis
double rxy=Math.Sqrt(aai+aaj);
double dx=ai/rxy;
double dy=aj/rxy;
double temp_xy=2.0*ar*rxy;
ai=temp_xy*dx;
aj=temp_xy*dy;
ar=aar-rxy*rxy;

// ar +=br;                ai +=bi;           aj +=bj;

    aai = ai * ai; aaj = aj * aj;
ai=2.0*ai*aj;
aj=aaj-aai;

// 2. Mandelbrot of (x,y)-z Axis
    aar = ar * ar; aai = ai * ai; aaj = aj * aj;
rxy=Math.Sqrt(aai+aaj);
dx=ai/rxy;
dy=aj/rxy;
temp_xy=2.0*ar*rxy;
ai=temp_xy*dx;
aj=temp_xy*dy;
ar=aar-rxy*rxy;

/*
// 3. Mandelbrot of (x,y)-z Axis
    aar = ar * ar; aai = ai * ai; aaj = aj * aj;
rxy=Math.Sqrt(aai+aaj);
dx=ai/rxy;
dy=aj/rxy;
temp_xy=2.0*ar*rxy;
ai=temp_xy*dx;
aj=temp_xy*dy;
ar=aar-rxy*rxy;
*/

  

 aai = ai * ai; aaj = aj * aj;
ai=2.0*ai*aj;
aj=aaj-aai;

/*
 aai = ai * ai; aaj = aj * aj;
ai=2.0*ai*aj;
aj=aaj-aai;
*/

                ar +=br;
                ai +=bi;
                aj +=bj;


                aar = ar * ar; aai = ai * ai; aaj = aj * aj;
                r = Math.Sqrt(aar + aai + aaj);


if(n>3 && n<50) {

if(ar>0)
red+=ar/r;
if(ai>0)
green+=ai/r;
if(aj>0)
blue+=aj/r;
}

                if (r > gr) { tw = n; break; }

            }

additionalPointInfo.red=red;
additionalPointInfo.green=green;
additionalPointInfo.blue=blue;


            if (invers) {
                if (tw == 0)
                    tw = 1;
                else
                    tw = 0;
            }
            return (tw);

        }
