Entry.Value:
public override void Init() { 
  base.Init();
 }

public override long InSet(double ar, double ai, double aj,  double br, double bi, double bj, double bk, long zkl, bool invers) {


  double aar, aai, aaj;
  int n;
  aar = ar * ar; aai = ai * ai; aaj = aj * aj;
  long tw = 0;
  double r=0;
double gr=3;
double minr=1000;

if(bj>0)
 return 1;

  additionalPointInfo=new AdditionalPointInfo();
  additionalPointInfo.red=0;
  additionalPointInfo.green=0;
  additionalPointInfo.blue=0;


  for (n = 1; n < zkl; n++) {
    ai=2.0*ar*ai-bi;
    ar=aar-aai-br;
aar=ar*ar;
aai=ai*ai;
    r = Math.Sqrt(aar + aai);

if(n>4 && r<minr)
  minr=r;

   // additionalPointInfo.blue=aaj;
    if (r > gr) { tw = n; break; }
  }

    additionalPointInfo.red=1;
    additionalPointInfo.green=minr;
 //  additionalPointInfo.green=0;



  if (invers) {
     if (tw == 0)
        tw = 1;
      else
        tw = 0;
  }
  return (tw);
}
