Entry.Value:
/// <summary>
        /// Initialisierung
        /// </summary>
        public override void Init() {
            base.Init();
            // Hier kann z.B. pow oder gr aus den Einstellungen gelesen werden.
additionalPointInfo=new AdditionalPointInfo();
          dzkl=GetDouble("Formula.Static.Cycles");
gr1=dzkl;
int tempGr=(int)gr1;
gr1=gr1- tempGr;
gr1=1-gr1;
gr1*=2.4;
 dzkl2=GetDouble("Formula.Static.MinCycle");
gr2=dzkl2;
tempGr=(int)gr2;
gr2=gr2- tempGr;
gr2=1-gr2;
gr2*=2.4;
}

double gr1=0;
double gr2=0;
double dzkl=0;
double dzkl2=0;



        public override long InSet(double ar, double ai, double aj,  double br, double bi, double bj, double bk, long zkl, bool invers) {
            double aar, aai, aaj;
            long tw;
            int n;
            int pow = 8; // n=8 entspricht dem Mandelbulb
                double gr =Math.Pow(10,gr1)+1.0;  //  Bailout
if(invers)
   gr =Math.Pow(10,gr2)+1.0;    

double ddiff=dzkl-((int)dzkl);
if(invers)
  ddiff=dzkl2-((int)dzkl2);

       double theta, phi;

            double r_n = 0;
            aar = ar * ar; aai = ai * ai; aaj = aj * aj;
            tw = 0L;
            double r = Math.Sqrt(aar + aai + aaj);

             double      phi_pow;
             double       theta_pow;
             double    sin_theta_pow;
              double  rn_sin_theta_pow;

additionalPointInfo.red=0;
additionalPointInfo.green=0;
additionalPointInfo.blue=0;
int colordiff=1; 
int colorzklTest=0;
int colorrange=4;
            for (n = 1; n < zkl; n++) {

                theta = Math.Atan2(Math.Sqrt(aar + aai), aj);
                phi = Math.Atan2(ai, ar);
                r_n = Math.Pow(r, pow);

                phi_pow=phi*pow;
                theta_pow=theta*pow;
                sin_theta_pow=Math.Sin(theta_pow);
               rn_sin_theta_pow=r_n* sin_theta_pow;

                ar =  rn_sin_theta_pow * Math.Cos(phi_pow)+br;
                ai = rn_sin_theta_pow * Math.Sin(phi_pow)+bi;
                aj = r_n * Math.Cos(theta_pow)+bj;

                aar = ar * ar; aai = ai * ai; aaj = aj * aj;
                r = aar + aai + aaj;

colorzklTest=colordiff+n;

if(colorzklTest==zkl-1-colorrange) {
additionalPointInfo.red+=(1.0-ddiff)*aar/r;
additionalPointInfo.green+=(1.0-ddiff)*aai/r;
additionalPointInfo.blue+=(1.0-ddiff)*aaj/r;
}


if(colorzklTest>zkl-1-colorrange && colorzklTest<zkl-1) {
additionalPointInfo.red+=aar/r;
additionalPointInfo.green+=aai/r;
additionalPointInfo.blue+=aaj/r;
}

if(colorzklTest==zkl-1) {
additionalPointInfo.red+=(ddiff)*aar/r;
additionalPointInfo.green+=(ddiff)*aai/r;
additionalPointInfo.blue+=(ddiff)*aaj/r;
}


r = Math.Sqrt(r);

                if (r > gr) { tw = n; break; }

            }

            if (invers) {
                if (tw == 0)
                    tw = 1;
                else
                    tw = 0;
            }

            return (tw);

        }
